<?php// require_once('class.JavaScriptPacker.php');require_once('Minify/CSS/Compressor.php');require_once('Minify/HTML.php');class Compressor {    function javascript($text) {        $packer = new JavaScriptPacker($text, 62, true, false);        return $packer->pack();    }    function css($text) {        return Minify_CSS_Compressor::process($text, array());    }    function html($text) {        return Minify_HTML::minify($text, array('xhtml'=>true));    }}