<?phperror_reporting(0);if(empty($scriptProperties['files'])) {    return $modx->error->failure($modx->lexicon('statiker.files_err_ns'));}$batch = $modx->getOption('batch',$scriptProperties,'');if(empty($batch)) return $modx->error->failure($modx->lexicon('statiker.action_err_ns'));require_once($modx->statiker->config['corePath'].'lib/statikerbuilder.class.php');$builder = new StatikerBuilder($modx);$fileIds = explode(',', $scriptProperties['files']);foreach($fileIds as $fileId) {    switch($batch) {        case 'remove':            $builder->removeFile($fileId);            break;    }}return $modx->error->success();