<?phpif(empty($scriptProperties['resource'])) return $modx->error->failure($modx->lexicon('statiker.file_err_ns'));require_once($modx->statiker->config['corePath'].'lib/statikerbuilder.class.php');$builder = new StatikerBuilder($modx);if($statikerFile = $builder->removeFile($scriptProperties['resource'])) {    return $modx->error->success('', $statikerFile);} else {    return $modx->error->failure($modx->lexicon('statiker.file_err_remove'));}