<?phpif(empty($scriptProperties['id'])) return $modx->error->failure($modx->lexicon('statiker.site_err_ns'));$site = $modx->getObject('statikerSite',$scriptProperties['id']);if(empty($site)) return $modx->error->failure($modx->lexicon('statiker.site_err_nf')); if($site->remove() == false) {    return $modx->error->failure($modx->lexicon('statiker.site_err_remove'));} return $modx->error->success('',$site);