<?php/* get Site */if(empty($scriptProperties['id'])) return $modx->error->failure($modx->lexicon('statiker.site_err_ns'));$site = $modx->getObject('statikerSite',$scriptProperties['id']);if(empty($site)) return $modx->error->failure($modx->lexicon('statiker.site_err_nf')); /* set Site fields */$site->fromArray($scriptProperties); /* save Site */if($site->save() == false) {    return $modx->error->failure($modx->lexicon('statiker.site_err_save'));} return $modx->error->success('',$site);