<?php/* parse JSON */if (empty($scriptProperties['data'])) return $modx->error->failure('Invalid data.');$_DATA = $modx->fromJSON($scriptProperties['data']);if (!is_array($_DATA)) return $modx->error->failure('Invalid data.'); /* get obj */if (empty($_DATA['id'])) return $modx->error->failure($modx->lexicon('statiker.site_err_ns'));$site = $modx->getObject('statikerSite',$_DATA['id']);if (empty($site)) return $modx->error->failure($modx->lexicon('statiker.site_err_nf')); $site->fromArray($_DATA); /* save */if ($site->save() == false) {    return $modx->error->failure($modx->lexicon('statiker.site_err_save'));} return $modx->error->success('',$site);